// App.js

import React, { useState } from 'react';
import './Signup.css';

function LoginForm() {
  return (
    <div className="login-box">
      <input type="email" className="email ele" placeholder="youremail@email.com" />
      <input type="password" className="password ele" placeholder="password" />
      <button className="clkbtn">Login</button>
    </div>
  );
}

function SignupForm() {
  return (
    <div className="signup-box">
      <input type="text" className="name ele" placeholder="Enter your name" />
      <input type="email" className="email ele" placeholder="youremail@email.com" />
      <input type="password" className="password ele" placeholder="password" />
      <input type="password" className="password ele" placeholder="Confirm password" />
      <button className="clkbtn">Signup</button>
    </div>
  );
}

function App() {
  const [isLoginFormVisible, setIsLoginFormVisible] = useState(true);

  const toggleForm = () => {
    setIsLoginFormVisible(!isLoginFormVisible);
  };

  return (
    <div className="container">
      <header>
        <h1 className="heading">GeeksforGeeks</h1>
        <h3 className="title">Sliding Login & Registration Form</h3>
      </header>

      <div className="slider"></div>
      <div className="btn">
        <button className={isLoginFormVisible ? "login active" : "login"} onClick={toggleForm}>Login</button>
        <button className={!isLoginFormVisible ? "signup active" : "signup"} onClick={toggleForm}>Signup</button>
      </div>

      <div className="form-section">
        {isLoginFormVisible ? <LoginForm /> : <SignupForm />}
      </div>
    </div>
  );
}

export default App;
