// Signup.js

import React, { useState } from 'react';
import axios from 'axios';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Check if the email is available
      const response = await axios.post('/api/check-email', { email });
      if (!response.data.available) {
        setError('Email is already in use');
        return;
      }
      
      // If email is available, proceed with user registration
      await axios.post('/api/signup', { email, password });
      
      // Redirect to login page or handle automatically logging in
      // For simplicity, let's assume the user is redirected to the login page
      // You can use React Router's useHistory hook for redirection
      // history.push('/login');
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Password:</label>
          <input 
            type="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
        </div>
        {error && <p>{error}</p>}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default Signup;
